  id: string;
  username: string;
  displayName: string;
  email: string;

  // Enhanced profile fields
  profilePicture: string;
  avatarDescription: string;
  avatarDetails: AvatarDetails;

  // Progress tracking
  level: number;
  xp: number;
  totalXp: number;

  // Social features
  bio: string;
  achievements: Achievement[];
  socialConnections: SocialConnection[];
  communityMemberships: string[]; // IDs of communities
