import { UserProfile } from '../types/UserProfile';

export function calculateLevelProgress(profile: UserProfile): number {
  return Math.min(Math.round((profile.xp / profile.totalXp) * 100), 100);
}

export function getAvatarDisplayName(profile: UserProfile): string {
  return profile.displayName || profile.username;
}

export function getProfileSummary(profile: UserProfile): string {
  return `${getAvatarDisplayName(profile)} - Level ${profile.level} - ${profile.bio.substring(0, 50)}${profile.bio.length > 50 ? '...' : ''}`;
}
