export const ProfilePage: React.FC = () => {
  const [profile, setProfile] = useState<UserProfile | null>(null);
  const [loading, setLoading] = useState(true);
  const router = useRouter();
  const { userId } = router.query;

  useEffect(() => {
    const loadProfile = () => {
      if (userId) {
        // Load specific user profile
        const userProfile = UserSession.getUserById(userId as string);
        if (userProfile) {
          setProfile(userProfile);
        } else {
          // Fallback to current user or redirect
          const currentUser = UserSession.getCurrentUser();
          if (currentUser) {
            setProfile(currentUser);
          } else {
            router.push('/login');
          }
        }
      } else {
        // No userId provided, load current user
        const currentUser = UserSession.getCurrentUser();
        if (currentUser) {
          setProfile(currentUser);
        } else {
          router.push('/login');
        }
      }
      setLoading(false);
    };

    loadProfile();
  }, [userId, router]);

  if (loading) {
    return <div className="loading-spinner">Loading...</div>;
  }

  if (!profile) {
    return <div className="error-message">User profile not found</div>;
  }

  return (
    <div className="profile-container">
      <ProfileHeader profile={profile} />
      <XpProgressSection current={profile.xp} total={profile.totalXp} level={profile.level} />
      <AchievementsSection achievements={profile.achievements || []} />
