3. Implement the rewards display component:
```tsx
// src/components/rewards/RewardsContainer.tsx
import React, { useState, useEffect } from 'react';
import { RewardsGrid } from './RewardsGrid';
import { RewardsState, Reward } from '../../types/Rewards';
import { UserProfile } from '../../types/UserProfile';

interface RewardsContainerProps {
  userProfile: UserProfile;
}

export const RewardsContainer: React.FC<RewardsContainerProps> = ({ userProfile }) => {
  const [rewardsState, setRewardsState] = useState<RewardsState>({
    availableRewards: [],
    claimedRewards: [],
    upcomingRewards: []
  });

  useEffect(() => {
    // Fetch rewards and categorize them based on user XP
    const fetchRewards = async () => {
      // In production, this would be an API call
      const allRewards = await getRewardsData();

      const available = allRewards.filter(reward => 
        !reward.claimed && userProfile.totalXp >= reward.xpRequired
      );

      const claimed = allRewards.filter(reward => 
        reward.claimed
      );

      const upcoming = allRewards.filter(reward => 
        !reward.claimed && userProfile.totalXp < reward.xpRequired
      ).sort((a, b) => a.xpRequired - b.xpRequired);

      setRewardsState({
        availableRewards: available,
        claimedRewards: claimed,
        upcomingRewards: upcoming
      });
    };

    fetchRewards();
  }, [userProfile]);

  const handleClaimReward = async (rewardId: string) => {
    // In production, this would be an API call
    await claimReward(rewardId);

    // Update local state
    setRewardsState(prev => {
      const reward = prev.availableRewards.find(r => r.id === rewardId);
      if (!reward) return prev;

      const updatedReward = {
        ...reward,
        claimed: true,
        claimDate: new Date().toISOString()
      };

      return {
        availableRewards: prev.availableRewards.filter(r => r.id !== rewardId),
        claimedRewards: [...prev.claimedRewards, updatedReward],
        upcomingRewards: prev.upcomingRewards
      };
    });
  };

  return (
    <div className="rewards-container">
      <h2 className="rewards-title">Your Rewards</h2>

      <RewardsGrid 
        availableRewards={rewardsState.availableRewards}
        upcomingRewards={rewardsState.upcomingRewards}
        claimedRewards={rewardsState.claimedRewards}
        userXp={userProfile.totalXp}
        onClaimReward={handleClaimReward}
      />
    </div>
  );
};
```

